mapper.select:
select t1.DOC_ID as docId , t1.DOC_ACCOUNT as docAccount , t1.STATUS as status , t2.INFO_ID as infoId , t2.DOC_NAME as docName , t2.DOC_PIC as docImg , t3.HOP_NAME as hopName , t4.LOC_NAME as locName , t5.NAME as docTitle, t.service_Id as serviceId, t.SERVICE_PRICE as servicePrice, t6.NAME as serviceTypeName
from DOC_SERVICE t left join DOC_INFO t2 on t.DOCINFO_ID=t2.INFO_ID left join DOC_USER t1 on t1.DOC_ID=t2.DOC_ID left join APP_HOP t3 on t2.DOC_HOPID=t3.HOP_ID left join APP_LOC t4 on t2.DOC_LOCID=t4.LOC_ID left join sys_dictionaries t5 on t2.DOC_TITLE=t5.DICTIONARIES_ID left join sys_dictionaries t6 on t.SERVICE_TYPE=t6.DICTIONARIES_ID
and ( t2.DOC_NAME like CONCAT('%',#{pd.keywords},'%') or t1.DOC_ACCOUNT like CONCAT('%',#{pd.keywords},'%') )
and t2.DOC_HOPID=#{pd.DOC_HOPID}
and t2.DOC_LOCID=#{pd.DOC_LOCID}
and t2.DOC_TITLE=#{pd.DOC_TITLE}
